#!/usr/bin/env python3
"""Load fonts from disk."""

import base64
from io import BytesIO


# From https://www.fontspace.com/minecraft-font-f28180
MINECRAFT_BOLD = BytesIO(base64.b64decode( ''))
MINECRAFT_ITALIC = BytesIO(base64.b64decode(''))

# Function to convert a font file to a base64-encoded byte array
def font_to_byte_array(font_path):
    with open(font_path, 'rb') as file:
        font_bytes = file.read()
        font_base64 = base64.b64encode(font_bytes)
        return font_base64

if '__main__' == __name__:
    # Replace with the actual path to Arial font
    arial_font_byte_array = font_to_byte_array('path/to/your/font.otf')
    print(arial_font_byte_array)